import React, {useState, useEffect} from 'react'
import useEventListener from '@use-it/event-listener'

const Player = () => {

    const [position, setPosition] = useState({
        xCurrent: 0,
        xPrevious: 0,
        yCurrent: 0,
        yPrevious: 0
    })
    const [facing, setFacing] = useState(0)
    const [step, setStep] = useState(0)

    const size = 77
    const direction = {
        down: 0,
        left: size*1,
        right: size*2,
        up: size*3
    }

    useEffect(() => {
    let stepInterval = setInterval(() => {
        if(step >= size*8) {
            setStep(0)
        }
        setStep(step + 1*size)
        if(step >= size*8) {
            setStep(0)
            clearInterval(stepInterval)
        }


        //--
            if(position.xCurrent < 0) {
                setPosition({...position,
                    current: position.xPrevious,
                    ...position,


                })
            }
        //--
    }, 80)

    return () => clearInterval(stepInterval)
    })

    useEventListener("keydown", ({ code }) => {
        console.log(code);
        if(code.indexOf("Arrow") === -1) return

        if(code==="ArrowRight") {
            setPosition(prevState => ({
                xCurrent: prevState.xCurrent+38,
                xPrevious: prevState.xCurrent
            }))
            setFacing(direction.right)
        }
        if(code==="ArrowLeft") {
            setPosition(prevState => ({
                xCurrent: prevState.xCurrent-38,
                xPrevious: prevState.xCurrent
            }))
            setFacing(direction.left)
        }
        if(code==="ArrowDown") {            
            setPosition(prevState => ({
                yCurrent: prevState.yCurrent+38,
                yPrevious: prevState.yCurrent
            }))
            setFacing(direction.down)
        }
        if(code==="ArrowUp") {
            setPosition(prevState => ({
                yCurrent: prevState.yCurrent-38,
                yPrevious: prevState.yCurrent
            }))
            setFacing(direction.up)
        }
    })

    return <>
    <div id="player" style={{
        position: "relative",
        width: 77,
        height: 77,
        left: position.xCurrent,
        top: position.yCurrent,
        background: `url(assets/player.png) -${step}px -${facing}px`
    }}></div>
    </>
}

export default Player
