import React, {useState, useEffect} from 'react'
import useEventListener from '@use-it/event-listener'

const Player = () => {

    const [position, setPosition] = useState({
        xCurrent: 0,
        xPrevious: 0,
        yCurrent: 0,
        yPrevious: 0
    })

    const [facing, setFacing] = useState(0)
    const [step, setStep] = useState(0)

    const playerSize = 77
    const direction = {
        down: 0,
        left: playerSize*1,
        right: playerSize*2,
        up: playerSize*3
    }

    useEffect(() => {
    let stepInterval = setInterval(() => {
        if(step >= playerSize*8) {
            setStep(0)
        }
        setStep(step + 1*playerSize)
        if(step >= playerSize*8) {
            setStep(0)
            clearInterval(stepInterval)
        }

        //-- próba zablokowania wychodzenia za mapę
            if(position.xCurrent < 0) {
                setPosition({...position,
                    xCurrent: position.xPrevious,

                })
            }
        //---------------------

    }, 80)

    return () => clearInterval(stepInterval)
    })

    useEventListener("keydown", ({ code }) => {
        console.log(code);
        if(code.indexOf("Arrow") === -1) return

        if(code==="ArrowRight") {
            if(position.xCurrent+77<=1078) {
                setPosition(prevState => ({
                    ...position,
                    xCurrent: prevState.xCurrent+77,
                    xPrevious: prevState.xCurrent
                }
                ))

            }    


            setFacing(direction.right)
    }
        if(code==="ArrowLeft") {
            if(position.xCurrent-77<0) {
                setPosition(prevState => ({
                    ...position,
                    xCurrent: prevState.xCurrent-77,
                    xPrevious: prevState.xCurrent
                }
                ))
            }
            setFacing(direction.left)
        }
        if(code==="ArrowDown") {            
            setPosition(prevState => ({
                ...position,
                yCurrent: prevState.yCurrent+77,
                yPrevious: prevState.yCurrent
            }))
            setFacing(direction.down)
        }
        if(code==="ArrowUp") {
            setPosition(prevState => ({
                ...position,
                yCurrent: prevState.yCurrent-77,
                yPrevious: prevState.yCurrent
            }))
            setFacing(direction.up)
        }
    })

    return <>
    <div id="player" style={{
        position: "relative",
        width: 77,
        height: 77,
        left: position.xCurrent,
        top: position.yCurrent,
        background: `url(assets/player.png) -${step}px -${facing}px`
    }}></div>
    </>
}

export default Player
